mapper.select:
SELECT CVPL_NO, DATE_FORMAT(CVPL_DT,'%Y년 %m월 %d일 %h시') AS CVPL_DT, CPTTR, CPTTR_CTTPC, CVPL_LC, CVPL_CN, DATE_FORMAT(REGIST_DT,'%Y년 %m월 %d일 %h시') AS REGIST_DT, REGISTER_ID, CHANGE_DT, CHANGER_ID, LEGALDONG_CODE FROM CVPL_MANAGE WHERE DATE_FORMAT(CVPL_DT,'%Y%m%d') >= #{startDate} AND DATE_FORMAT(CVPL_DT,'%Y%m%d') <= #{endDate}
AND CVPL_CN LIKE CONCAT('%', #{branchName}, '%')
AND LEGALDONG_CODE LIKE CONCAT('%', #{town}, '%')

	  SELECT SENSE_EVL_NO,POINT_X,POINT_Y 
  		FROM SHP_SENSE_EVL_REAL A
	LEFT OUTER JOIN SENSE_EVL B
  		  ON A.SENSE_EVL_ = B.SENSE_EVL_NO
  	WHERE BSML_FQ >= #{startOU}
	  AND BSML_FQ <= #{endOU}
