mapper.delete:
	DELETE FROM ANALS_AREA_MANAGE
      WHERE GRID_INTRST_SE_CODE = 'ANL01002' 
	  AND GRID_AREA_ID = #{indexId}

mapper.select:
SELECT CONCAT(A.LEGALDONG_CODE,A.SPOT_CODE) AS CODE, B.OPR_STTUS_CODE, SENSOR_NM, A.LA AS POINT_Y, A.LO AS POINT_X, MESURE_DT, DATE, VOCS, CCNT, NH3, ERCRT, H2S, ARCSR, OU, SOLRAD, HD, TMPRT, WD, WS, NO2, SO2, PM10, PM2_5, CFC, CH3SH, TMA, ETHANOL FROM (SELECT * FROM bdsm.IOT_SENSOR_INFO WHERE SENSOR_TY_CODE = 'SEN01001' AND SPOT_CODE NOT IN ('0003') AND LEGALDONG_CODE LIKE CONCAT(#{town},'%') AND LO IS NOT NULL
AND SENSOR_NM LIKE CONCAT('%', #{branchName}, '%')
) AS A LEFT OUTER JOIN (SELECT A.SPOT_CODE,DATE_FORMAT(MESURE_DT,'%Y%m%d%H%i') AS DATE, DATE_FORMAT(MESURE_DT,'%Y년 %m월 %d일 %H시 %i분') AS MESURE_DT, OPR_STTUS_CODE, TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='1' THEN MESURE_DNSTY END),3) AS 'VOCS', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='2' THEN MESURE_DNSTY END),3) AS 'CCNT', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='3' THEN MESURE_DNSTY END),3) AS 'NH3', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='4' THEN MESURE_DNSTY END),3) AS 'ERCRT', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='5' THEN MESURE_DNSTY END),3) AS 'H2S', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='6' THEN MESURE_DNSTY END),3) AS 'ARCSR', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='7' THEN MESURE_DNSTY END),3) AS 'OU', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='8' THEN MESURE_DNSTY END),3) AS 'SOLRAD', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='9' THEN MESURE_DNSTY END),3) AS 'HD', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='10' THEN MESURE_DNSTY END),3) AS 'TMPRT', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='11' THEN MESURE_DNSTY END),3) AS 'WD', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='12' THEN MESURE_DNSTY END),3) AS 'WS', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='13' THEN MESURE_DNSTY END),3) AS 'NO2', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='14' THEN MESURE_DNSTY END),3) AS 'SO2', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='15' THEN MESURE_DNSTY END),3) AS 'PM10', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='16' THEN MESURE_DNSTY END),3) AS 'PM2_5', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='17' THEN MESURE_DNSTY END),3) AS 'CFC', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='18' THEN MESURE_DNSTY END),3) AS 'CH3SH', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='19' THEN MESURE_DNSTY END),3) AS 'TMA', TRUNCATE(SUM(CASE WHEN A.SENSOR_SPCS_NO='20' THEN MESURE_DNSTY END),3) AS 'ETHANOL' FROM bdsm.IOT_SENSOR_MESURE_INFO A, bdsm.IOT_SENSOR_SPCS_INFO B, bdsm.IOT_MESURE_IEM_MANAGE C WHERE CONCAT(A.LEGALDONG_CODE,A.SPOT_CODE,A.SENSOR_SPCS_NO) = CONCAT(B.LEGALDONG_CODE,B.SPOT_CODE,B.SENSOR_SPCS_NO) AND B.IEM_CODE = C.IEM_CODE AND DATE_FORMAT(MESURE_DT,'%Y%m%d%H%i') = CONCAT(#{startDate},#{startTime},#{startMinute}) GROUP BY OPR_STTUS_CODE,A.SPOT_CODE,CONCAT(A.LEGALDONG_CODE,A.SPOT_CODE),OPR_STTUS_CODE,MESURE_DNSTY,MESURE_DT) AS B ON A.SPOT_CODE = B.SPOT_CODE

	SELECT STATION_ID,POS,NAME, POINT_X, POINT_Y
                FROM bdsm.SHP_OBSR_SPOT
			   WHERE STATION_ID = #{STATION_ID}
